     * @param tokenAddress the address of the token contract
     * @param from The sender of the token
     * @param to The recipient of the token
     */
    function transfer(
        uint256 amount,
        Enums.TokenType tokenType,
        uint256 tokenId,
        address tokenAddress,
        address from,
        address to
    ) public onlyOperators whenNotPaused {
        if (tokenType == Enums.TokenType.ERC20) {
            bool success = ERC20(tokenAddress).transferFrom(from, to, amount);
            if (success) {
                emit TransferSuccessful(
                    amount,
                    tokenType,
                    tokenId,
                    tokenAddress,
                    from,
                    to
                );
            } else {
                revert TransferFailed(
                    amount,
                    tokenType,
                    tokenId,
                    tokenAddress,
                    from,
                    to,
                    "ERC20 transfer failure"
                );
            }
        } else if (tokenType == Enums.TokenType.ERC721) {
            ERC721(tokenAddress).safeTransferFrom(from, to, tokenId);
            emit TransferSuccessful(
                amount,
                tokenType,
                tokenId,
                tokenAddress,
                from,
                to
            );
        } else if (tokenType == Enums.TokenType.ERC1155) {
            ERC1155(tokenAddress).safeTransferFrom(
                from,
                to,
                tokenId,
                amount,
                ""
            );
            emit TransferSuccessful(
                amount,
                tokenType,
                tokenId,
                tokenAddress,
                from,
                to
            );
        } else {
            revert TransferFailed(
                amount,
                tokenType,
                tokenId,
                tokenAddress,
                from,
                to,
                "Invalid tokenType"
            );
        }
    }
}

contracts/lib/SafeCast.sol.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.9;

library SafeCast {
    function toUint200(uint256 value) internal pure returns (uint200) {
        require(value < 2**200, "value does not fit in 200 bits");
        return uint200(value);
    }

    function toUint128(uint256 value) internal pure returns (uint128) {
        require(value < 2**128, "value does not fit in 128 bits");
        return uint128(value);
    }

    function toUint40(uint256 value) internal pure returns (uint40) {
        require(value < 2**40, "value does not fit in 40 bits");
        return uint40(value);
    }

    function toUint8(uint256 value) internal pure returns (uint8) {
        require(value < 2**8, "value does not fit in 8 bits");
        return uint8(value);
    }
}

